function getTamanhoSecaoDisplay(tamanhoSecaoVar) {
  // Verifica se o valor é um número
  // isNaN(tamanhoSecaoVar) retorna true se não for um número
  // !isNaN(parseFloat(tamanhoSecaoVar)) verifica se pode ser parseado como número
  // typeof tamanhoSecaoVar === 'number' é mais direto para números já tipados
  // No WeWeb, muitas vezes os inputs vêm como string, mesmo que sejam números.
  // Por isso, parseFloat é importante.
  
  if (typeof tamanhoSecaoVar === 'number') {
    return "PERSONALIZADO";
  }

  // Se o valor for uma string que representa um número, como "50"
  // parseFloat() tenta converter a string em um número.
  // isNaN() verifica se o resultado dessa conversão é "Not-a-Number".
  // Se não for Not-a-Number, significa que era um número (em string).
  if (typeof tamanhoSecaoVar === 'string' && !isNaN(parseFloat(tamanhoSecaoVar)) && isFinite(tamanhoSecaoVar)) {
    return "PERSONALIZADO";
  }

  // Se não for um número (nem como tipo number, nem como string de número), retorna o próprio valor
  return tamanhoSecaoVar;
}

